import React, { Component } from 'react';
import AddItem from './AddItem';
import CartItemComponent from './CartItemComponent';

class CartItems extends Component {



    state = {currentProduct:{}, currentQuantity: 0}

    getQuantity = (e) =>{
        e.preventDefault();
        this.setState({currentQuantity : e.target.getValue})
        console.log(this.state.currentQuantity)
    };

    render() {
        return (
            <div className="container">
                <h1>Cart Items</h1>
                <div className="list-group">
                    <div className="list-group-item">
                        <div className="row">
                            <div className="col-md-8">Product</div>
                            <div className="col-md-2">Price</div>
                            <div className="col-md-2">Quantity</div>
                        </div>
                    </div>
                    {this.props.products.cartItemList.map(product => <CartItemComponent key={product.id} product={product} />)}

                </div>

                <div>
                    <form>
                        <div className="mb-3">
                            <label className="form-label">Quantity</label>
                            <input className="form-control" onChange={this.getQuantity}></input>

                        </div>
                        <div className="mb-3">
                            <label className="form-label">Products</label>
                            <select className="form-select" aria-label="Default select example">
                                {this.props.products.products.map(product => <AddItem key={product.id} product={product.name} />)}

                            </select>
                        </div>

                        <button type="submit" className="btn btn-primary" >Submit</button>
                    </form>

                </div>

            </div>
        );
    }

}






export default CartItems;
