import React, { Component } from "react";


export default class AddItem extends React.Component {

    state = {}

    getObject = (e) =>{
        e.preventDefault();
        this.setState({object : {
            id: e.target.value.id,
            name: e.target.value.name,
            priceInCents: e.target.value.priceInCents
        }})
        this.props.addProduct(e, this.state.object)
    }

    render() {
        return (
            <div className="mb-3">
                <label className="form-label">Products</label>
                <select className="form-select" aria-label="Default select example" onChange={this.getObject} name="currentProduct">
                    {this.props.products.map(product => <option value={product}>{product.name}</option>)}

                </select>
            </div>



        )

    };


}
