import './App.css';
import CartItems from './components/CartItems';
import CartHeader from './components/CartHeader';
import CartFooter from './components/CartFooter';


class App extends React.Component() {
  state = {products: 
    [
      {product: { id: 40, name: 'Mediocre Iron Watch', priceInCents: 399 }, quantity: 1 },
      {product: { id: 41, name: 'Heavy Duty Concrete Plate', priceInCents: 499 }, quantity: 2},
      {product: { id: 42, name: 'Intelligent Paper Knife', priceInCents: 1999 }, quantity: 2},
      {product: { id: 43, name: 'Small Aluminum Keyboard', priceInCents: 2500 }, quantity: 2},
      {product: { id: 44, name: 'Practical Copper Plate', priceInCents: 1000 }, quantity: 2},
      {product: { id: 45, name: 'Awesome Bronze Pants', priceInCents: 399 }, quantity: 2},
      {product: { id: 46, name: 'Intelligent Leather Clock', priceInCents: 2999 }, quantity: 2},
      {product: { id: 47, name: 'Ergonomic Bronze Lamp', priceInCents: 40000 }, quantity: 2},
      {product: { id: 48, name: 'Awesome Leather Shoes', priceInCents: 3990 }, quantity: 2}
    ]  
  }

  render(){
    <div>
          <CartHeader /> 
          <CartItems products={this.state}/>
          <CartFooter copyright= "2016" />

      </div>
  };


}

export default App;
