import React, { Component } from 'react';
import AddItem from './AddItem';
import CartItemComponent from './CartItemComponent';

class CartItems extends Component {



    state = {currentProduct:{}, currentQuantity: 0}

    getQuantity = async(e) =>{
        e.preventDefault();
        await this.setState({[e.target.name] : e.target.value}) 
        console.log(this.state.currentQuantity)
        console.log(this.state.currentProduct)
    };

    addProduct = (e) => {
        e.preventDefault();
        var tempProduct = {
            id: this.props.products.cartItemList.length +1,
            product: this.state.currentProduct,
            quantity: this.state.currentQuantity
        }

        this.props.addItemToList(tempProduct)
    }



    render() {
        return (
            <div className="container">
                <h1>Cart Items</h1>
                <div className="list-group">
                    <div className="list-group-item">
                        <div className="row">
                            <div className="col-md-8">Product</div>
                            <div className="col-md-2">Price</div>
                            <div className="col-md-2">Quantity</div>
                        </div>
                    </div>
                    {this.props.products.cartItemList.map(product => <CartItemComponent key={product.id} product={product} />)}

                </div>

                <div>
                    <form>
                        <div className="mb-3">
                            <label className="form-label">Quantity</label>
                            <input className="form-control" onChange={this.getQuantity} name="currentQuantity"></input>

                        </div>
                        <div className="mb-3">
                            <label className="form-label">Products</label>
                            <select className="form-select" aria-label="Default select example" onChange={this.getQuantity} name="currentProduct">
                                {this.props.products.products.map(product => <AddItem key={product.id} product={product} />)}

                            </select>
                        </div>

                        <button type="submit" className="btn btn-primary" onSubmit={this.addProduct}>Submit</button>
                    </form>

                </div>

            </div>
        );
    }

}






export default CartItems;
