import React from 'react';
import AddItem from './AddItem';

import CartItemComponent from './CartItemComponent';

const CartItems = ({ products }) => {
    return (
        <div className="container">
            <h1>Cart Items</h1>
            <div className="list-group">
                <div className="list-group-item">
                    <div className="row">
                        <div className="col-md-8">Product</div>
                        <div className="col-md-2">Price</div>
                        <div className="col-md-2">Quantity</div>
                    </div>
                </div>
                {products.cartItemList.map(product => <CartItemComponent key= {product.id} product={product} />)}

            </div>

            <div>
                <form>
                    <div className="mb-3">
                        <label className="form-label">Quantity</label>
                        <input  className="form-control" ></input>

                    </div>
                    <div className="mb-3">
                        <label className="form-label">Products</label>
                        <select className="form-select" aria-label="Default select example">
                            {products.products.map(product => <AddItem key= {product.id} product={product.name} />)}

                        </select>
                    </div>

                    <button type="submit" className="btn btn-primary" onSubmit={AddItemToList(submitHandler)}>Submit</button>
                </form>

            </div>

        </div>
    );
}

const submitHandler = (e) => {
    e.preventDefault();
    return{
        product: {
          id: 40,
          name: 'Mediocre Iron Watch',
          priceInCents: 399
        },
        quantity: 1
      }
}

export default CartItems;
