import React, { Component } from "react";


export default class AddItem extends React.Component {

    state = {}



    render() {
        return (
            <div className="mb-3">
                <label className="form-label">Products</label>
                <select className="form-select" aria-label="Default select example" onChange={this.getProduct} name="currentProduct">
                    {this.props.products.map(product => <option value={product}>{product.name}</option>)}

                </select>
            </div>



        )

    };


}
